 * those functions `private`, and then adding `external` `nonReentrant` entry
 * points to them.
 *
 * TIP: If EIP-1153 (transient storage) is available on the chain you're deploying at,
 * consider using {ReentrancyGuardTransient} instead.
 *
 * TIP: If you would like to learn more about reentrancy and alternative ways
 * to protect against it, check out our blog post
 * https://blog.openzeppelin.com/reentrancy-after-istanbul/[Reentrancy After Istanbul].
 */
abstract contract ReentrancyGuard {
    // Booleans are more expensive than uint256 or any type that takes up a full
    // word because each write operation emits an extra SLOAD to first read the
    // slot's contents, replace the bits taken up by the boolean, and then write
    // back. This is the compiler's defense against contract upgrades and
    // pointer aliasing, and it cannot be disabled.

    // The values being non-zero value makes deployment a bit more expensive,
    // but in exchange the refund on every call to nonReentrant will be lower in
    // amount. Since refunds are capped to a percentage of the total
    // transaction's gas, it is best to keep them low in cases like this one, to
    // increase the likelihood of the full refund coming into effect.
    uint256 private constant NOT_ENTERED = 1;
    uint256 private constant ENTERED = 2;

    uint256 private _status;

    /**
     * @dev Unauthorized reentrant call.
     */
    error ReentrancyGuardReentrantCall();

    constructor() {
        _status = NOT_ENTERED;
    }

    /**
     * @dev Prevents a contract from calling itself, directly or indirectly.
     * Calling a `nonReentrant` function from another `nonReentrant`
     * function is not supported. It is possible to prevent this from happening
     * by making the `nonReentrant` function external, and making it call a
     * `private` function that does the actual work.
     */
    modifier nonReentrant() {
        _nonReentrantBefore();
        _;
        _nonReentrantAfter();
    }

    function _nonReentrantBefore() private {
        // On the first call to nonReentrant, _status will be NOT_ENTERED
        if (_status == ENTERED) {
            revert ReentrancyGuardReentrantCall();
        }

        // Any calls to nonReentrant after this point will fail
        _status = ENTERED;
    }

    function _nonReentrantAfter() private {
        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = NOT_ENTERED;
    }

    /**
     * @dev Returns true if the reentrancy guard is currently set to "entered", which indicates there is a
     * `nonReentrant` function in the call stack.
     */
    function _reentrancyGuardEntered() internal view returns (bool) {
        return _status == ENTERED;
    }
}

contracts/AutoUnpausable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title AutoUnpausable
 * @notice Pausable with automatic unpause after max duration
 * @dev Pause can last max 7 days. Governance can extend by re-pausing.
 */
abstract contract AutoUnpausable is AccessControl {
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    uint256 public constant MAX_PAUSE_DURATION = 7 days;

    uint256 public pausedUntil;

    event Paused(address indexed account, uint256 until);
    event Unpaused(address indexed account);

    /**
     * @notice Check if contract is currently paused
     */
    function paused() public view returns (bool) {
        return block.timestamp < pausedUntil;
    }

    /**
     * @notice Pause the contract for up to MAX_PAUSE_DURATION
     * @param duration Pause duration in seconds (max 7 days)
     */
    function pause(uint256 duration) external onlyRole(PAUSER_ROLE) {
        require(duration > 0 && duration <= MAX_PAUSE_DURATION, "Invalid duration");
        pausedUntil = block.timestamp + duration;
        emit Paused(msg.sender, pausedUntil);
    }

    /**
     * @notice Manually unpause before auto-unpause
     */
    function unpause() external onlyRole(PAUSER_ROLE) {
        require(paused(), "Not paused");
        pausedUntil = 0;
        emit Unpaused(msg.sender);
    }

    /**
     * @notice Modifier to check pause status
     */
    modifier whenNotPaused() {
        require(!paused(), "Contract is paused");
        _;
    }

    modifier whenPaused() {
        require(paused(), "Contract is not paused");
        _;
    }
}

contracts/ISNADOracle.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./AutoUnpausable.sol";

/**
 * @title ISNADOracle
 * @notice Detection verdicts and jury system for ISNAD protocol
 * 
 * Security notes:
 * - Jury selection uses block hash for randomness (upgradeable to VRF)
 * - Jurors cannot have stake in flagged resource
 * - Supermajority (2/3) required for verdict
 * - Appeals require 2x deposit
 * - Slash only callable after guilty verdict
 * - AutoUnpausable for emergency pause (max 7 days)
 */
contract ISNADOracle is AutoUnpausable, ReentrancyGuard {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    // ============ Structs ============

    enum FlagStatus {
        Pending,      // Awaiting jury
        InReview,     // Jury deliberating
        Guilty,       // Verdict: malicious
        Innocent,     // Verdict: safe
        Appealed,     // Under appeal
        Dismissed     // Flag dismissed (spam/invalid)
    }

    struct Flag {
        bytes32 resourceHash;
        address flagger;
        uint256 deposit;
        bytes32 evidenceHash;     // IPFS/Arweave hash of evidence
        FlagStatus status;
        uint256 createdAt;
        uint256 juryDeadline;
        uint256 voteCount;
        uint256 guiltyVotes;
    }

    struct Juror {
        bool selected;
        bool voted;
        bool votedGuilty;
    }

    // ============ State ============

    // Staking contract reference (for checking stakes and slashing)
    address public stakingContract;

    // Minimum deposit to flag a resource (prevents spam)
    uint256 public minFlagDeposit = 100 * 1e18; // 100 $ISNAD

    // Jury size
    uint256 public jurySize = 5;

    // Voting period (seconds)
    uint256 public votingPeriod = 7 days;

    // Supermajority threshold (basis points, 6667 = 66.67%)
    uint256 public supermajorityBps = 6667;

    // Appeal deposit multiplier (2x)
    uint256 public appealMultiplier = 2;

    // Flag storage
    mapping(bytes32 => Flag) public flags;           // flagId => Flag
    mapping(bytes32 => mapping(address => Juror)) public jurors;  // flagId => juror => Juror
    mapping(bytes32 => address[]) public juryList;   // flagId => selected jurors

    // Track active flags per resource
    mapping(bytes32 => bytes32) public activeFlag;   // resourceHash => flagId

    // Eligible juror pool (addresses that have attested to other resources)
    address[] public jurorPool;
    mapping(address => bool) public isInJurorPool;

    // ============ Events ============

    event ResourceFlagged(
        bytes32 indexed flagId,
        bytes32 indexed resourceHash,
        address indexed flagger,
        uint256 deposit,
        bytes32 evidenceHash
    );

    event JurySelected(bytes32 indexed flagId, address[] jurors);

    event JurorVoted(bytes32 indexed flagId, address indexed juror, bool guilty);

    event VerdictReached(
        bytes32 indexed flagId,
        bytes32 indexed resourceHash,
        FlagStatus verdict,
        uint256 guiltyVotes,
        uint256 totalVotes
    );

    event FlagAppealed(bytes32 indexed flagId, address indexed appellant, uint256 deposit);

    event SlashExecuted(bytes32 indexed resourceHash, uint256 totalSlashed);

    // ============ Constructor ============

    constructor(address _stakingContract) {
        require(_stakingContract != address(0), "Invalid staking contract");
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        stakingContract = _stakingContract;
    }

    // ============ External Functions ============

    /**
     * @notice Flag a resource as potentially malicious
     * @param resourceHash The hash of the resource to flag
     * @param evidenceHash IPFS/Arweave hash containing evidence
     */
    function flagResource(
        bytes32 resourceHash,
        bytes32 evidenceHash
    ) external payable nonReentrant whenNotPaused returns (bytes32 flagId) {
        require(msg.value >= minFlagDeposit, "Insufficient deposit");
        require(activeFlag[resourceHash] == bytes32(0), "Resource already flagged");

        flagId = keccak256(abi.encodePacked(resourceHash, msg.sender, block.timestamp));

        flags[flagId] = Flag({
            resourceHash: resourceHash,
            flagger: msg.sender,
            deposit: msg.value,
            evidenceHash: evidenceHash,
            status: FlagStatus.Pending,
            createdAt: block.timestamp,
            juryDeadline: 0,
            voteCount: 0,
            guiltyVotes: 0
        });

        activeFlag[resourceHash] = flagId;

        emit ResourceFlagged(flagId, resourceHash, msg.sender, msg.value, evidenceHash);

        // Auto-select jury if pool is large enough
        if (jurorPool.length >= jurySize) {
            _selectJury(flagId);
        }
    }

    /**
     * @notice Select jury for a pending flag
     * @param flagId The flag to select jury for
     */
    function selectJury(bytes32 flagId) external {
        Flag storage flag = flags[flagId];
        require(flag.status == FlagStatus.Pending, "Not pending");
        require(jurorPool.length >= jurySize, "Insufficient juror pool");

        _selectJury(flagId);
    }

    /**
     * @notice Cast vote as juror
     * @param flagId The flag being voted on
     * @param guilty True if voting guilty
     */
    function vote(bytes32 flagId, bool guilty) external nonReentrant whenNotPaused {
        Flag storage flag = flags[flagId];
        require(flag.status == FlagStatus.InReview, "Not in review");
        require(block.timestamp < flag.juryDeadline, "Voting ended");

        Juror storage juror = jurors[flagId][msg.sender];
        require(juror.selected, "Not a juror");
        require(!juror.voted, "Already voted");

        juror.voted = true;
        juror.votedGuilty = guilty;
        flag.voteCount++;
        if (guilty) {
            flag.guiltyVotes++;
        }

        emit JurorVoted(flagId, msg.sender, guilty);

        // Check if all votes are in
        if (flag.voteCount == jurySize) {
            _finalizeVerdict(flagId);
        }
    }

    /**
     * @notice Finalize verdict after voting period
     * @param flagId The flag to finalize
     */
    function finalizeVerdict(bytes32 flagId) external {
        Flag storage flag = flags[flagId];
        require(flag.status == FlagStatus.InReview, "Not in review");
        require(block.timestamp >= flag.juryDeadline, "Voting not ended");

        _finalizeVerdict(flagId);
    }

    /**
     * @notice Appeal a verdict
     * @param flagId The flag to appeal
     */
    function appeal(bytes32 flagId) external payable nonReentrant {
        Flag storage flag = flags[flagId];
        require(
            flag.status == FlagStatus.Guilty || flag.status == FlagStatus.Innocent,
            "Cannot appeal"
        );
        require(block.timestamp < flag.juryDeadline + 3 days, "Appeal window closed");

        uint256 requiredDeposit = flag.deposit * appealMultiplier;
        require(msg.value >= requiredDeposit, "Insufficient appeal deposit");

        flag.status = FlagStatus.Appealed;
        flag.deposit += msg.value;
        flag.voteCount = 0;
        flag.guiltyVotes = 0;

        // Clear old jury
        delete juryList[flagId];

        emit FlagAppealed(flagId, msg.sender, msg.value);

        // Select new jury
        if (jurorPool.length >= jurySize) {
            _selectJury(flagId);
        }
    }

    /**
     * @notice Execute slash after guilty verdict (called by staking contract)
     * @param resourceHash The resource to slash
     */
    function executeSlash(bytes32 resourceHash) external returns (bool) {
        bytes32 flagId = activeFlag[resourceHash];
        require(flagId != bytes32(0), "No active flag");

        Flag storage flag = flags[flagId];
        require(flag.status == FlagStatus.Guilty, "Not guilty");

        // Clear active flag
        activeFlag[resourceHash] = bytes32(0);

        // Return deposit to flagger (they were right)
        payable(flag.flagger).transfer(flag.deposit);

        emit SlashExecuted(resourceHash, flag.deposit);

        return true;
    }

    // ============ Admin Functions ============

    /**
     * @notice Add address to juror pool
     * @param jurorAddress Address to add
     */
    function addToJurorPool(address jurorAddress) external onlyRole(ADMIN_ROLE) {
        require(!isInJurorPool[jurorAddress], "Already in pool");
        jurorPool.push(jurorAddress);
        isInJurorPool[jurorAddress] = true;
    }

    /**
     * @notice Batch add addresses to juror pool
     * @param addresses Addresses to add
     */
    function batchAddToJurorPool(address[] calldata addresses) external onlyRole(ADMIN_ROLE) {
        for (uint i = 0; i < addresses.length; i++) {
            if (!isInJurorPool[addresses[i]]) {
                jurorPool.push(addresses[i]);
                isInJurorPool[addresses[i]] = true;
            }
        }
    }

    /**
     * @notice Update staking contract address
     */
    function setStakingContract(address _stakingContract) external onlyRole(ADMIN_ROLE) {
        require(_stakingContract != address(0), "Invalid staking contract");
        stakingContract = _stakingContract;
    }

    /**
     * @notice Update minimum flag deposit
     */
    function setMinFlagDeposit(uint256 _minDeposit) external onlyRole(ADMIN_ROLE) {
        minFlagDeposit = _minDeposit;
    }

    /**
     * @notice Update jury size
     */
    function setJurySize(uint256 _jurySize) external onlyRole(ADMIN_ROLE) {
        require(_jurySize >= 3 && _jurySize % 2 == 1, "Invalid jury size");
        jurySize = _jurySize;
    }

    /**
     * @notice Update voting period
     */
    function setVotingPeriod(uint256 _votingPeriod) external onlyRole(ADMIN_ROLE) {
        require(_votingPeriod >= 1 days, "Period too short");
        votingPeriod = _votingPeriod;
    }

    // ============ View Functions ============

    function getFlag(bytes32 flagId) external view returns (Flag memory) {
        return flags[flagId];
    }

    function getJury(bytes32 flagId) external view returns (address[] memory) {
        return juryList[flagId];
    }

    function getJurorPoolSize() external view returns (uint256) {
        return jurorPool.length;
    }

    function isJuror(bytes32 flagId, address addr) external view returns (bool) {
        return jurors[flagId][addr].selected;
    }

    function hasVoted(bytes32 flagId, address addr) external view returns (bool) {
        return jurors[flagId][addr].voted;
    }

    // ============ Internal Functions ============

    function _selectJury(bytes32 flagId) internal {
        Flag storage flag = flags[flagId];

        // Simple random selection using block hash
        // TODO: Upgrade to Chainlink VRF for production
        uint256 seed = uint256(keccak256(abi.encodePacked(
            blockhash(block.number - 1),
            flagId,
            block.timestamp
        )));

        address[] memory selected = new address[](jurySize);
        uint256 selectedCount = 0;
        uint256 attempts = 0;
        uint256 maxAttempts = jurorPool.length * 3;

        while (selectedCount < jurySize && attempts < maxAttempts) {
            uint256 index = (seed + attempts) % jurorPool.length;
            address candidate = jurorPool[index];

            // Check if already selected or has conflict
            bool valid = !jurors[flagId][candidate].selected;

            // TODO: Check if candidate has stake in the flagged resource
            // This requires calling the staking contract

            if (valid) {
                selected[selectedCount] = candidate;
                jurors[flagId][candidate].selected = true;
                selectedCount++;
            }

            attempts++;
        }

        require(selectedCount == jurySize, "Could not select enough jurors");

        for (uint i = 0; i < jurySize; i++) {
            juryList[flagId].push(selected[i]);
        }

        flag.status = FlagStatus.InReview;
        flag.juryDeadline = block.timestamp + votingPeriod;

        emit JurySelected(flagId, selected);
    }

    function _finalizeVerdict(bytes32 flagId) internal {
        Flag storage flag = flags[flagId];

        // Calculate if supermajority reached
        uint256 guiltyThreshold = (flag.voteCount * supermajorityBps) / 10000;
        uint256 innocentThreshold = (flag.voteCount * supermajorityBps) / 10000;

        if (flag.guiltyVotes >= guiltyThreshold) {
            flag.status = FlagStatus.Guilty;
        } else if ((flag.voteCount - flag.guiltyVotes) >= innocentThreshold) {
            flag.status = FlagStatus.Innocent;
            // Return deposit to flagger minus fee
            uint256 fee = flag.deposit / 10; // 10% fee for failed flag
            payable(flag.flagger).transfer(flag.deposit - fee);
        } else {
            // No supermajority - dismiss
            flag.status = FlagStatus.Dismissed;
            // Return half deposit
            payable(flag.flagger).transfer(flag.deposit / 2);
        }

        emit VerdictReached(
            flagId,
            flag.resourceHash,
            flag.status,
            flag.guiltyVotes,
            flag.voteCount
        );
    }
}

contracts/ISNADRewardPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./AutoUnpausable.sol";

/**
 * @title ISNADRewardPool
 * @notice Yield distribution for auditors who stake on clean resources
 * 
 * Reward mechanics:
 * - Rewards accrue based on stake amount and lock duration
 * - Longer locks earn higher multiplier (1x-3x)
 * - Rewards come from protocol fees and inflation
 * - Claimed rewards are minted from ISNADToken
 * - AutoUnpausable for emergency pause (max 7 days)
 */
contract ISNADRewardPool is AutoUnpausable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant DISTRIBUTOR_ROLE = keccak256("DISTRIBUTOR_ROLE");

    // ============ State ============

    IERC20 public immutable isnadToken;
    address public stakingContract;

    // Reward rate per second per token staked (in wei)
    // Default: ~10% APY = 0.1 / (365 * 24 * 3600)  3.17e-9 tokens/sec/token
    uint256 public rewardRatePerSecond = 3170979198; // ~10% APY in wei terms

    // Lock duration multipliers (in basis points, 10000 = 1x)
    // 30 days = 1x, 90 days = 1.5x, 180 days = 2x, 365 days = 3x
    mapping(uint256 => uint256) public lockMultipliers;

    // Auditor rewards tracking
    struct AuditorReward {
        uint256 totalEarned;
        uint256 totalClaimed;
        uint256 lastUpdateTime;
        uint256 pendingRewards;
    }

    mapping(address => AuditorReward) public auditorRewards;

    // Total distributed (for stats)
    uint256 public totalDistributed;
    uint256 public totalClaimed;

    // ============ Events ============

    event RewardAccrued(address indexed auditor, uint256 amount);
    event RewardClaimed(address indexed auditor, uint256 amount);
    event RewardRateUpdated(uint256 newRate);
    event LockMultiplierUpdated(uint256 duration, uint256 multiplier);

    // ============ Constructor ============

    constructor(address _isnadToken, address _stakingContract) {
        require(_isnadToken != address(0), "Invalid token");
        require(_stakingContract != address(0), "Invalid staking contract");
        isnadToken = IERC20(_isnadToken);
        stakingContract = _stakingContract;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(DISTRIBUTOR_ROLE, _stakingContract);

        // Initialize lock multipliers
        lockMultipliers[30 days] = 10000;   // 1x
        lockMultipliers[60 days] = 12500;   // 1.25x
        lockMultipliers[90 days] = 15000;   // 1.5x
        lockMultipliers[180 days] = 20000;  // 2x
        lockMultipliers[365 days] = 30000;  // 3x
    }

    // ============ External Functions ============

    /**
     * @notice Accrue rewards for an auditor's attestation
     * @dev Called by staking contract when attestation is created or modified
     * @param auditor The auditor address
     * @param stakeAmount Amount staked
     * @param lockDuration Lock duration in seconds
     */
    function accrueRewards(
        address auditor,
        uint256 stakeAmount,
        uint256 lockDuration
    ) external onlyRole(DISTRIBUTOR_ROLE) {
        _updateRewards(auditor);

        // Calculate reward for this stake
        uint256 multiplier = _getMultiplier(lockDuration);
        uint256 reward = (stakeAmount * rewardRatePerSecond * lockDuration * multiplier) / (10000 * 1e18);

        auditorRewards[auditor].pendingRewards += reward;
        auditorRewards[auditor].totalEarned += reward;
        totalDistributed += reward;

        emit RewardAccrued(auditor, reward);
    }

    /**
     * @notice Claim accumulated rewards
     */
    function claimRewards() external nonReentrant returns (uint256) {
        _updateRewards(msg.sender);

        AuditorReward storage reward = auditorRewards[msg.sender];
        uint256 claimable = reward.pendingRewards;
        require(claimable > 0, "No rewards to claim");

        reward.pendingRewards = 0;
        reward.totalClaimed += claimable;
        totalClaimed += claimable;

        // Transfer rewards (pool must have sufficient balance)
        isnadToken.safeTransfer(msg.sender, claimable);

        emit RewardClaimed(msg.sender, claimable);

        return claimable;
    }

    /**
     * @notice Calculate pending rewards for an auditor
     * @param auditor The auditor address
     */
    function pendingRewards(address auditor) external view returns (uint256) {
        return auditorRewards[auditor].pendingRewards;
    }

    /**
     * @notice Get auditor's reward info
     * @param auditor The auditor address
     */
    function getAuditorRewards(address auditor) external view returns (
        uint256 earned,
        uint256 claimed,
        uint256 pending
    ) {
        AuditorReward storage reward = auditorRewards[auditor];
        return (reward.totalEarned, reward.totalClaimed, reward.pendingRewards);
    }

    /**
     * @notice Get lock duration multiplier
     * @param lockDuration Duration in seconds
     */
    function getMultiplier(uint256 lockDuration) external view returns (uint256) {
        return _getMultiplier(lockDuration);
    }

    // ============ Admin Functions ============

    /**
     * @notice Update reward rate
     * @param newRate New rate per second per token (in wei)
     */
    function setRewardRate(uint256 newRate) external onlyRole(ADMIN_ROLE) {
        rewardRatePerSecond = newRate;
        emit RewardRateUpdated(newRate);
    }

    /**
     * @notice Update lock multiplier
     * @param duration Lock duration in seconds
     * @param multiplier Multiplier in basis points (10000 = 1x)
     */
    function setLockMultiplier(
        uint256 duration,
        uint256 multiplier
    ) external onlyRole(ADMIN_ROLE) {
        require(multiplier >= 10000, "Multiplier must be >= 1x");
        lockMultipliers[duration] = multiplier;
        emit LockMultiplierUpdated(duration, multiplier);
    }

    /**
     * @notice Update staking contract
     */
    function setStakingContract(address _stakingContract) external onlyRole(ADMIN_ROLE) {
        require(_stakingContract != address(0), "Invalid staking contract");
        // Revoke old, grant new
        _revokeRole(DISTRIBUTOR_ROLE, stakingContract);
        stakingContract = _stakingContract;
        _grantRole(DISTRIBUTOR_ROLE, _stakingContract);
    }

    /**
     * @notice Emergency withdraw (admin only)
     * @param amount Amount to withdraw
     */
    function emergencyWithdraw(uint256 amount) external onlyRole(ADMIN_ROLE) {
        isnadToken.safeTransfer(msg.sender, amount);
    }

    /**
     * @notice Fund the reward pool
     * @param amount Amount to fund
     */
    function fund(uint256 amount) external {
        isnadToken.safeTransferFrom(msg.sender, address(this), amount);
    }

    // ============ Internal Functions ============

    function _updateRewards(address auditor) internal {
        auditorRewards[auditor].lastUpdateTime = block.timestamp;
    }

    function _getMultiplier(uint256 lockDuration) internal view returns (uint256) {
        // Find the highest applicable multiplier
        if (lockDuration >= 365 days && lockMultipliers[365 days] > 0) {
            return lockMultipliers[365 days];
        } else if (lockDuration >= 180 days && lockMultipliers[180 days] > 0) {
            return lockMultipliers[180 days];
        } else if (lockDuration >= 90 days && lockMultipliers[90 days] > 0) {
            return lockMultipliers[90 days];
        } else if (lockDuration >= 60 days && lockMultipliers[60 days] > 0) {
            return lockMultipliers[60 days];
        } else if (lockDuration >= 30 days && lockMultipliers[30 days] > 0) {
            return lockMultipliers[30 days];
        }

        // Default 1x for shorter durations
        return 10000;
    }

    // ============ View Functions ============

    function poolBalance() external view returns (uint256) {
        return isnadToken.balanceOf(address(this));
    }
}

contracts/ISNADStaking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./AutoUnpausable.sol";

/**
 * @title ISNADStaking
 * @notice Manages attestations (stakes) on ISNAD resources
 * @dev 
 * Security considerations:
 * - ReentrancyGuard on stake/unstake
 * - Lock periods prevent flash loan exploits
 * - Only ORACLE_ROLE can slash
 * - Whale caps prevent concentration attacks
 * - BURNER_ROLE on ISNADToken required for slashing
 * - AutoUnpausable for emergency pause (max 7 days)
 */
contract ISNADStaking is ReentrancyGuard, AutoUnpausable {
    using SafeERC20 for IERC20;

    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    IERC20 public immutable token;

    // Attestation data
    struct Attestation {
        address auditor;
        bytes32 resourceHash;
        uint256 amount;
        uint256 lockUntil;
        uint256 lockDuration;  // in seconds
        bool slashed;
    }

    // Whale caps
    uint256 public constant MAX_STAKE_PER_AUDITOR = 10_000 * 1e18;
    uint256 public constant MAX_STAKE_PERCENT = 33; // 33% of total stake on resource (when multiple stakers)

    // Lock duration tiers (in seconds)
    uint256 public constant MIN_LOCK_DURATION = 7 days;
    uint256 public constant MEDIUM_LOCK_DURATION = 30 days;
    uint256 public constant MAX_LOCK_DURATION = 90 days;

    // Trust score thresholds (stake amount in wei)
    uint256 public constant TIER_1_THRESHOLD = 100 * 1e18;    // Community
    uint256 public constant TIER_2_THRESHOLD = 1_000 * 1e18;  // Verified
    uint256 public constant TIER_3_THRESHOLD = 10_000 * 1e18; // Trusted

    // Attestation storage
    mapping(bytes32 => Attestation) public attestations; // attestationId => Attestation
    mapping(bytes32 => uint256) public resourceTotalStake; // resourceHash => total
    mapping(address => uint256) public auditorTotalStake;  // auditor => total across all resources
    mapping(address => mapping(bytes32 => uint256)) public auditorResourceStake; // auditor => resourceHash => stake
    mapping(address => bytes32[]) public auditorAttestations; // auditor => attestationIds
    mapping(bytes32 => bytes32[]) public resourceAttestations; // resourceHash => attestationIds

    uint256 public totalStaked;

    // Events
    event Staked(
        bytes32 indexed attestationId,
        address indexed auditor,
        bytes32 indexed resourceHash,
        uint256 amount,
        uint256 lockUntil,
        uint256 lockDuration
    );

    event Unstaked(
        bytes32 indexed attestationId,
        address indexed auditor,
        uint256 amount
    );

    event Slashed(
        bytes32 indexed attestationId,
        address indexed auditor,
        bytes32 indexed resourceHash,
        uint256 amount
    );

    /**
     * @notice Deploy the staking contract
     * @param _token Address of the ISNAD token
     * @param admin Address that will receive admin role
     */
    constructor(address _token, address admin) {
        require(_token != address(0), "Invalid token address");
        require(admin != address(0), "Invalid admin address");

        token = IERC20(_token);
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(PAUSER_ROLE, admin);
    }

    /**
     * @notice Stake tokens on a resource (create attestation)
     * @param resourceHash The content hash of the resource being attested to
     * @param amount Amount of tokens to stake
     * @param lockDuration How long to lock tokens (in seconds)
     * @return attestationId Unique identifier for this attestation
     */
    function stake(
        bytes32 resourceHash,
        uint256 amount,
        uint256 lockDuration
    ) external nonReentrant whenNotPaused returns (bytes32 attestationId) {
        require(resourceHash != bytes32(0), "Invalid resource hash");
        require(amount > 0, "Amount must be positive");
        require(lockDuration >= MIN_LOCK_DURATION, "Lock too short");
        require(lockDuration <= MAX_LOCK_DURATION, "Lock too long");

        // Check whale caps
        require(
            auditorTotalStake[msg.sender] + amount <= MAX_STAKE_PER_AUDITOR,
            "Exceeds max stake per auditor"
        );

        // Check concentration cap: no single auditor can have >33% of a resource's stake
        // (only enforced when there's existing stake from others)
        uint256 currentResourceTotal = resourceTotalStake[resourceHash];
        uint256 otherStakersAmount = currentResourceTotal - auditorResourceStake[msg.sender][resourceHash];
        if (otherStakersAmount > 0) {
            uint256 newAuditorStake = auditorResourceStake[msg.sender][resourceHash] + amount;
            uint256 newResourceTotal = currentResourceTotal + amount;
            uint256 auditorPercent = (newAuditorStake * 100) / newResourceTotal;
            require(auditorPercent <= MAX_STAKE_PERCENT, "Exceeds max stake percent");
        }

        // Generate unique attestation ID
        attestationId = keccak256(
            abi.encodePacked(msg.sender, resourceHash, block.timestamp, amount)
        );
        require(attestations[attestationId].amount == 0, "Attestation exists");

        uint256 lockUntil = block.timestamp + lockDuration;

        // Store attestation
        attestations[attestationId] = Attestation({
            auditor: msg.sender,
            resourceHash: resourceHash,
            amount: amount,
            lockUntil: lockUntil,
            lockDuration: lockDuration,
            slashed: false
        });

        // Update totals
        resourceTotalStake[resourceHash] += amount;
        auditorTotalStake[msg.sender] += amount;
        auditorResourceStake[msg.sender][resourceHash] += amount;
        totalStaked += amount;

        // Track attestation IDs
        auditorAttestations[msg.sender].push(attestationId);
        resourceAttestations[resourceHash].push(attestationId);

        // Transfer tokens
        token.safeTransferFrom(msg.sender, address(this), amount);

        emit Staked(attestationId, msg.sender, resourceHash, amount, lockUntil, lockDuration);
    }

    /**
     * @notice Unstake tokens after lock period expires
     * @param attestationId The attestation to unstake
     */
    function unstake(bytes32 attestationId) external nonReentrant {
        Attestation storage att = attestations[attestationId];

        require(att.auditor == msg.sender, "Not your attestation");
        require(!att.slashed, "Attestation slashed");
        require(att.amount > 0, "No stake found");
        require(block.timestamp >= att.lockUntil, "Still locked");

        uint256 amount = att.amount;
        bytes32 resourceHash = att.resourceHash;

        // Update totals
        resourceTotalStake[resourceHash] -= amount;
        auditorTotalStake[msg.sender] -= amount;
        auditorResourceStake[msg.sender][resourceHash] -= amount;
        totalStaked -= amount;

        // Clear attestation
        att.amount = 0;

        // Transfer tokens back
        token.safeTransfer(msg.sender, amount);

        emit Unstaked(attestationId, msg.sender, amount);
    }

    /**
     * @notice Slash an attestation (only ORACLE_ROLE)
     * @param attestationId The attestation to slash
     * @dev Burns tokens via ISNADToken.burnFrom (requires BURNER_ROLE)
     */
    function slash(bytes32 attestationId) external onlyRole(ORACLE_ROLE) nonReentrant {
        Attestation storage att = attestations[attestationId];

        require(!att.slashed, "Already slashed");
        require(att.amount > 0, "No stake found");

        uint256 amount = att.amount;
        address auditor = att.auditor;
        bytes32 resourceHash = att.resourceHash;

        // Mark as slashed
        att.slashed = true;
        att.amount = 0;

        // Update totals
        resourceTotalStake[resourceHash] -= amount;
        auditorTotalStake[auditor] -= amount;
        auditorResourceStake[auditor][resourceHash] -= amount;
        totalStaked -= amount;

        // Burn tokens (requires this contract to have BURNER_ROLE on token)
        // We call burnFrom on the token, but since tokens are already here,
        // we burn from this contract's balance
        // Note: ISNADToken.burnFrom expects tokens to be at the 'from' address
        // Since we hold the tokens, we need to burn from ourselves
        (bool success, ) = address(token).call(
            abi.encodeWithSignature("burnFrom(address,uint256)", address(this), amount)
        );
        require(success, "Burn failed");

        emit Slashed(attestationId, auditor, resourceHash, amount);
    }

    /**
     * @notice Get trust score for a resource
     * @param resourceHash The resource to check
     * @return score Total weighted stake (amount * lock multiplier)
     */
    function getTrustScore(bytes32 resourceHash) external view returns (uint256 score) {
        bytes32[] memory attIds = resourceAttestations[resourceHash];

        for (uint256 i = 0; i < attIds.length; i++) {
            Attestation storage att = attestations[attIds[i]];
            if (att.amount > 0 && !att.slashed) {
                // Weight by lock duration: 7d=1x, 30d=1.5x, 90d=2x
                uint256 multiplier = 100;
                if (att.lockDuration >= MAX_LOCK_DURATION) {
                    multiplier = 200;
                } else if (att.lockDuration >= MEDIUM_LOCK_DURATION) {
                    multiplier = 150;
                }
                score += (att.amount * multiplier) / 100;
            }
        }
    }

    /**
     * @notice Get trust tier for a resource
     * @param resourceHash The resource to check
     * @return tier 0=None, 1=Community, 2=Verified, 3=Trusted
     */
    function getTrustTier(bytes32 resourceHash) external view returns (uint8 tier) {
        uint256 score = this.getTrustScore(resourceHash);

        if (score >= TIER_3_THRESHOLD) return 3;
        if (score >= TIER_2_THRESHOLD) return 2;
        if (score >= TIER_1_THRESHOLD) return 1;
        return 0;
    }

    /**
     * @notice Get all attestations for a resource
     * @param resourceHash The resource to query
     * @return attestationIds Array of attestation IDs
     */
    function getResourceAttestations(bytes32 resourceHash) 
        external view returns (bytes32[] memory) 
    {
        return resourceAttestations[resourceHash];
    }

    /**
     * @notice Get all attestations by an auditor
     * @param auditor The auditor address
     * @return attestationIds Array of attestation IDs
     */
    function getAuditorAttestations(address auditor) 
        external view returns (bytes32[] memory) 
    {
        return auditorAttestations[auditor];
    }

    /**
     * @notice Get attestation details
     * @param attestationId The attestation ID
     */
    function getAttestation(bytes32 attestationId) 
        external view returns (
            address auditor,
            bytes32 resourceHash,
            uint256 amount,
            uint256 lockUntil,
            uint256 lockDuration,
            bool slashed
        ) 
    {
        Attestation storage att = attestations[attestationId];
        return (
            att.auditor,
            att.resourceHash,
            att.amount,
            att.lockUntil,
            att.lockDuration,
            att.slashed
        );
    }
}

